pragma solidity ^0.6.6;

import "./HoldMoney.sol";
import "./Sorter.sol";
import { MaticWETH } from "./tokens/MaticWETH.sol";
import { BetteryToken } from "./tokens/BetteryToken.sol";

contract PublicEvent is HoldMoney, Sorter {
    MaticWETH public maticToken;
    BetteryToken public betteryToken;

    uint8 private percentQuiz = 2;
    uint256 private sevenDaysTimeStamp = 604800;
    address payable public companyAddress;
    uint256 betteryAmount = 1000000000000000000;

    event eventIsFinish(int256 question_id, bool payEther, bool moneyOnEvent);
    event payEvent(string path, uint256 money, bool payEther, address wallet, string from, int256 question_id);
    event revertedEvent(int256 question_id, string purpose);

    struct Participant {
        address payable participants;
        uint256 amount;
    }

    struct Validator {
        address payable validators;
    }

    struct Question {
        int256 question_id;
        uint256 startTime;
        uint256 endTime;
        uint8 questionQuantity;
        uint256 pool;
        uint8 percentHost;
        uint8 percentValidator;
        mapping(uint256 => Participant[]) participant;
        mapping(uint256 => Validator[]) validator;
        uint256 validatorsAmount;
        bool expertsQuantityWay;
        uint256 activeValidators;
        address payable hostWallet;
        uint256 persentFeeCompany;
        uint256 persentFeeHost;
        uint256 persentForEachValidators;
        uint256 correctAnswer;
        address[] allParticipant;
        bool payEther;
        bool reverted;
    }

    mapping(int256 => Question) questions;

    constructor(MaticWETH _maticToken, BetteryToken _betteryToken) public {
        maticToken = _maticToken;
        betteryToken = _betteryToken;
        companyAddress = msg.sender;
    }

    function startQestion(
        int256 _question_id,
        uint256 _startTime,
        uint256 _endTime,
        uint8 _percentHost,
        uint8 _percentValidator,
        uint8 _questionQuantity,
        uint256 _validatorsAmount,
        bool _pathHoldMoney,
        bool _payEther,
        bool _expertsQuantityWay
    ) public payable {
        questions[_question_id].question_id = _question_id;
        questions[_question_id].endTime = _endTime;
        questions[_question_id].questionQuantity = _questionQuantity;
        questions[_question_id].startTime = _startTime;
        questions[_question_id].validatorsAmount = _validatorsAmount;
        questions[_question_id].hostWallet = _msgSender();
        questions[_question_id].payEther = _payEther;
        questions[_question_id].expertsQuantityWay = _expertsQuantityWay;

        if (_percentHost == 0) {
            questions[_question_id].percentHost = 3;
        } else {
            questions[_question_id].percentHost = _percentHost;
        }

        if (_percentValidator == 0) {
            questions[_question_id].percentValidator = 5;
        } else {
            questions[_question_id].percentValidator = _percentValidator;
        }

        // uint256 amount = _setMoneyRetention(_pathHoldMoney, _question_id, _msgSender());
        // if(_pathHoldMoney){
        //     require(maticToken.allowance(_msgSender(), address(this)) >= amount, "Do not enought Matic ethers");
        //     require(maticToken.transferFrom(_msgSender(), address(this), amount), "Transfer Matic ethers error");
        // }else{
        //     require(betteryToken.allowance(_msgSender(), address(this)) >= amount, "Do not enought Bettery tokens");
        //     require(betteryToken.transferFrom(_msgSender(), address(this), amount), "Transfer Bettery tokens error");
        // }
       // emit payEvent("send", amount, _pathHoldMoney, _msgSender(), "hold money", _question_id);
        emit payEvent("send", 0, _pathHoldMoney, _msgSender(), "hold money", _question_id);
    }

    function setAnswer(int256 _question_id, uint8 _whichAnswer, uint256 _amount) public payable {
        bool payEther = questions[_question_id].payEther;
        require(setTimeAnswer(_question_id) == 0, "Time is not valid");

        Participant memory parts;
        parts = Participant(_msgSender(), _amount);
        questions[_question_id].participant[_whichAnswer].push(parts);
        questions[_question_id].allParticipant.push(_msgSender());

        questions[_question_id].pool += _amount;

        if(payEther){
            require(maticToken.allowance(_msgSender(), address(this)) >= _amount, "Do not enought Matic ethers");
            require(maticToken.transferFrom(_msgSender(), address(this), _amount), "Transfer Matic ethers error");
        }else{
            require(betteryToken.allowance(_msgSender(), address(this)) >= _amount, "Do not enought Bettery tokens");
            require(betteryToken.transferFrom(_msgSender(), address(this), _amount), "Transfer Bettery tokens error");
        }

        emit payEvent("send", _amount, payEther, _msgSender(), "participant", _question_id);

    }

    function setValidator(int256 _question_id, uint8 _whichAnswer) public payable {
        require(setTimeValidator(_question_id) == 0, "Time is not valid");
        if(questions[_question_id].activeValidators == 0 && questions[_question_id].expertsQuantityWay == true){
            setValidatorPercent(_question_id);
        }
        Validator memory valid;
        valid = Validator(_msgSender());
        questions[_question_id].validator[_whichAnswer].push(valid);
        uint256 active = questions[_question_id].activeValidators + 1;
        if (active == questions[_question_id].validatorsAmount) {
            questions[_question_id].activeValidators = active;
            letsFindCorrectAnswer(_question_id);
        } else {
            questions[_question_id].activeValidators = active;
        }
    }

    function setValidatorPercent(int256 _question_id) private{
        uint256 validatorPercent = getPersent(
            questions[_question_id].allParticipant.length,
            10
        );
        if(validatorPercent <= 3){
           questions[_question_id].validatorsAmount = 3;
        }else{
            questions[_question_id].validatorsAmount = validatorPercent;
        }
    }

    function letsFindCorrectAnswer(int256 _question_id) private {
        uint256 biggestValue = 0;
        uint256 correctAnswer;
        uint8 questionQuantity = questions[_question_id].questionQuantity;

        // find correct answer
        for (uint8 i = 0; i < questionQuantity; i++) {
            if (questions[_question_id].validator[i].length > biggestValue) {
                biggestValue = questions[_question_id].validator[i].length;
                correctAnswer = i;
            }
        }

        findDuplicates(correctAnswer, biggestValue, _question_id, questionQuantity);
    }

    function findDuplicates(uint256 correctAnswer, uint256 biggestValue, int256 _question_id, uint8 questionQuantity) private {
        if(questions[_question_id].validatorsAmount > 1){
            uint8 duplicatesAnswers = 0;
            uint256[] memory allValidatorsAnswers = new uint[](questionQuantity);
            for (uint8 i = 0; i < questionQuantity; i++) {
                allValidatorsAnswers[i] = questions[_question_id].validator[i].length;
            }

            setSort(allValidatorsAnswers);
            sort();

            for (uint8 i = 0; i < sortedArray.length - 1; i++) {
                if((i+1) < sortedArray.length){
                    if (sortedArray[i + 1] == sortedArray[i]) {
                        if(biggestValue == sortedArray[i]){
                            duplicatesAnswers ++;
                        }
                    }
                }
            }

            if(duplicatesAnswers > 0){
                // TO DO
                revertedPayment(_question_id, "duplicates validators");
            }else{
                questions[_question_id].correctAnswer = correctAnswer;
                findLosersPool(correctAnswer, _question_id);
            }
        }else{
            questions[_question_id].correctAnswer = correctAnswer;
            findLosersPool(correctAnswer, _question_id);
        }

    }

    function findLosersPool(uint256 correctAnswer, int256 _question_id) private {
        bool payEther = questions[_question_id].payEther;
        if (questions[_question_id].pool > 0) {
            uint256 B;
            uint256 n = questions[_question_id].participant[correctAnswer].length;
            for (uint8 i = 0; i < n; i++) {
                B = B + questions[_question_id].participant[correctAnswer][i].amount;
            }
            uint256 loserPool = questions[_question_id].pool - B;
            if(loserPool == 0){
                revertedPayment(_question_id, "chosen only one answer");
            }else{
                letsPayCompanyFee(correctAnswer, _question_id, loserPool, payEther);
            }
        }else{
            emit eventIsFinish(_question_id, payEther, false);
        }
    }

    function letsPayCompanyFee(uint256 correctAnswer, int256 _question_id, uint256 loserPool, bool payEther) private {
        // pay fee for company
        uint256 persentFee = getPersent(
            loserPool,
            percentQuiz
        );
        if(payEther){
            require(maticToken.transfer(companyAddress, persentFee), "Transfer Matic Ether to company is error");
        }else{
            require(betteryToken.transfer(companyAddress, persentFee), "Transfer Bettery token to company is error");
        }
        questions[_question_id].persentFeeCompany = persentFee;

        letsPayHostFee(correctAnswer, _question_id, payEther, loserPool);
    }

    function letsPayHostFee(uint256 correctAnswer, int256 _question_id, bool payEther, uint256 loserPool) private {
        // pay fee for host
        uint256 persHostFee = getPersent(
            loserPool,
            questions[_question_id].percentHost
        );
        address payable hostWallet = questions[_question_id].hostWallet;
        if(payEther){require(maticToken.transfer(hostWallet, persHostFee), "Transfer Matic Ether to host is error");}
        else {require(betteryToken.transfer(hostWallet, persHostFee), "Transfer Bettery token to host is error");}
        questions[_question_id].persentFeeHost = persHostFee;

        letsPayValidatorFee(correctAnswer, _question_id, payEther, loserPool);
    }

    function letsPayValidatorFee(uint256 correctAnswer, int256 _question_id, bool payEther, uint256 loserPool) private {
        // calculate percent for validator
        uint256 persentForValidators = getPersent(
            loserPool,
            questions[_question_id].percentValidator
        );
        uint256 persentForEachValidators = persentForValidators / questions[_question_id].validator[correctAnswer].length;

        questions[_question_id].persentForEachValidators = persentForEachValidators;

        // pay for validatos
        for (uint8 i = 0; i < questions[_question_id].validator[correctAnswer].length; i++ ) {
            address payable _validator = questions[_question_id].validator[correctAnswer][i].validators;

            if(payEther){require(maticToken.transfer(_validator, persentForEachValidators), "Transfer Matic Ether to validators is error");}
            else {require(betteryToken.transfer(_validator, persentForEachValidators), "Transfer Bettery token to validators is error");}
        }

        letsPayParticipantFee(correctAnswer, _question_id, payEther, loserPool);
    }

    function letsPayParticipantFee(uint256 correctAnswer, int256 _question_id, bool payEther, uint256 loserPool) private{
        uint256 playersFee = getPersent(
            loserPool,
            90
        );
         // Pay for participant
        uint256 B;
        uint256 n = questions[_question_id].participant[correctAnswer].length;
        for (uint8 i = 0; i < n; i++) {
           B = B + questions[_question_id].participant[correctAnswer][i].amount;
        }

        for (uint8 i = 0; i < n; i++) {
           address payable participant = questions[_question_id].participant[correctAnswer][i].participants;
           uint256 Bn = questions[_question_id].participant[correctAnswer][i].amount;
           uint256 rBn = Bn + ((playersFee * Bn) / B);
           if(payEther){require(maticToken.transfer(participant, rBn), "Transfer Matic Ether to participant is error");}
           else {require(betteryToken.transfer(participant, rBn), "Transfer Bettery token to participant is error");}
        }

        // Pay for losers
        int256 questionQuantity = questions[_question_id].questionQuantity;
        for(uint8 i = 0; i < questionQuantity; i++){
            if(i != correctAnswer){
                for(uint8 z = 0; z < questions[_question_id].participant[i].length; z++){
                    address payable loserAddress = questions[_question_id].participant[i][z].participants;
                    require(betteryToken.transfer(loserAddress, betteryAmount), "Transfer for losers error");
                }
            }
        }
        questions[_question_id].reverted = false;
        emit eventIsFinish(_question_id, payEther, true);
    }

    function revertedPayment(int256 _question_id, string memory purpose) public{
        require(msg.sender == companyAddress, "Only owner can execute this function");
        bool payEther = questions[_question_id].payEther;
        int256 questionQuantity = questions[_question_id].questionQuantity;

        for (uint8 i = 0; i < questionQuantity; i++) {
          for(uint8 index = 0; index < questions[_question_id].participant[i].length; index++){
                address payable _participant = questions[_question_id].participant[i][index].participants;
                uint256 price = questions[_question_id].participant[i][index].amount;
                if(payEther){require(maticToken.transfer(_participant, price), "Revert Matic ether to host is error");}
                else {require(betteryToken.transfer(_participant, price), "Revert Bettery token to host is error");}
            }
        }
        questions[_question_id].reverted = true;
        emit revertedEvent(_question_id, purpose);
    }

    function getPersent(uint256 _percent, uint256 _from)
        private
        pure
        returns (uint256)
    {
        return (_from * _percent) / 100;
    }

    function getQuestion(int256 _question_id)
        public
        view
        returns (
            uint256 persentFeeCompany,
            uint256 persentForEachValidators,
            uint256 money,
            bool reverted,
            uint256 correctAnswer,
            uint256 persentFeeHost
        )
    {
        return (
            questions[_question_id].persentFeeCompany,
            questions[_question_id].persentForEachValidators,
            questions[_question_id].pool,
            questions[_question_id].reverted,
            questions[_question_id].correctAnswer,
            questions[_question_id].persentFeeHost
        );
    }

    function deleteEvent(int256 _question_id) public {
        require(
            deleteEventValidator(_question_id) == 0,
            "Delete event is false"
        );
        delete questions[_question_id];
    }

    function quizeBalance() public view returns (uint256) {
        return address(this).balance;
    }

    function getContractTokenBalance() public view returns (uint256) {
        return maticToken.balanceOf(address(this));
    }

    function getMoneyRetention(address payable _host) public payable {
        (uint256 amount, address host, bool path, int256 _question_id) = _getMoneyRetention(_host);
        if(path){require(maticToken.transfer(host, amount), "Transfer Matic ether for money retention is error");}
        else {require(betteryToken.transfer(host, amount), "Transfer Bettery token for money retention is error");}


        emit payEvent("receive", amount, path, host, "hold money", _question_id);
    }

 function amountGuard(bool _path, address payable wallet) public view returns (int8) {
     uint256 balanceToken;
     if(_path){balanceToken = maticToken.balanceOf(wallet);}
     else {balanceToken = betteryToken.balanceOf(wallet);}

     return _amountGuard(_path, balanceToken, wallet);
 }

 // VALIDATION

     function setTimeValidator(int256 _question_id) public view returns (int8) {
        if (int256(now - questions[_question_id].endTime) >= 0){
            for (uint8 i = 0; i < questions[_question_id].allParticipant.length; i++) {
                if (questions[_question_id].allParticipant[i] == _msgSender()) {
                    return 3; // user can't validate because he participates in the event
                }
            }
            // user can validate because time is valid.
            return 0;
        } else {
            // user can't validate because event is not started yet.
            return 1;
        }
    }

    function setTimeAnswer(int256 _question_id) public view returns (int8) {
        if (int256(now - questions[_question_id].startTime) >= 0) {
            if (int256(questions[_question_id].endTime - now) >= 0) {
                // user can make answer because time is valid.
                return 0;
            } else {
                // user can't make answer because event is finish.
                return 2;
            }
        } else {
            // user can't make answer because event is not started yet.
            return 1;
        }
    }

    function deleteEventValidator(int256 _question_id)
        public
        view
        returns (int8)
    {
        if (questions[_question_id].hostWallet == _msgSender()) {
            if (questions[_question_id].pool == 0) {
                // user can delete event
                return 0;
            } else {
                // user can't delete event because event has money on balance
                return 1;
            }
        } else {
            // only owner can delete event.
            return 2;
        }
    }
}
